      #ncham=self.get_name()
      #ncham=ncham+(8-len(ncham))*' '
      #valeur=numpy.array(aster.getvectjev(ncham+(19-len(ncham))*' '+'.VALE'), complex)
      valeur=numpy.array(self.sdj.VALE.get(), complex)

